**script.js**
```javascript
// 表示エリアのinput要素を取得する
const display = document.getElementById('result');

// 表示したテキストを保存する変数を定義する
let text = '';

// 処理結果を表示する関数を作成する
function appendText(text) {
    this.text += text;
    display.value = text;
}

// 表示されたテキストの計算処理を行う関数を作成する
function calculateResult() {
    try {
        const result = eval(display.value);
        display.value = result;
    } catch (e) {
        console.error(e);
        display.value = 'エラーが発生しました。';
    }
}

// 表示されたテキストをクリアする関数を作成する
function clearDisplay() {
    text = '';
    display.value = '';
}
```

